import React, { useState } from 'react';
import { FaSearch } from 'react-icons/fa';
import "./SearchBar.css"

function SearchBar() {
  const [query, setQuery] = useState('');

  const handleInputChange = (event) => {
    event.preventDefault();
    setQuery(event.target.value);
    fetch(`https://api.themoviedb.org/3/search/movie?api_key=4e44d9029b1270a757cddc766a1bcb63&language=en-US&query=${e.target.value}`)
    .then((res)=>res.json())
    .then((data)=>{
      console.log(`Searching for ${data}`);
    })
  };

  // const handleKeyDown = (event) => {
  //   if (event.key === 'Enter') {
  //     handleSearch();
  //   }
  // };

  return (
    <div className="taskbar">
      <div className="search-box">
        <input type="text" placeholder="Search..." value={query} onChange={handleInputChange} />
        <button type="button" onClick={handleInputChange}><FaSearch /></button>
      </div>
    </div>
  );
}

export default SearchBar;
