import React, { useState } from 'react';
import { FaSearch } from 'react-icons/fa';
import "./SearchBar.css"
import { Link } from "react-router-dom"

function SearchBar() {
  const [query, setQuery] = useState('');
  const [results,setResults]=useState([])

  const handleInputChange = (event) => {
    event.preventDefault();
    setQuery(event.target.value);
    fetch(`https://api.themoviedb.org/3/search/movie?api_key=4e44d9029b1270a757cddc766a1bcb63&language=en-US&query=${event.target.value}`)
    .then((res)=>res.json())
    .then((data)=>{
      if(!data.errors){
        setResults(data.results);
      }else{
        setResults([])
      };
    })
  };

  return (
    <div className="taskbar">
      <div className="search-box">
        <input type="text" placeholder="Search..." value={query} onChange={handleInputChange} />
        <button type="button"><FaSearch /></button>
      </div>

      <div className='searchcontainer'>
        <ul className='ul'>
          {results.map((movie)=>(
          <Link to={`/movie/${movie.id}`} style={{textDecoration:"none"}}><li>{movie.title}</li></Link>
        ))
        }</ul>
}</div>
      
    </div>
  );
}

export default SearchBar;
